<?php//FUNÇÃO PARA CALCULAR O VALOR DA CONTAfunction calcula_conta($conexao,$id_conta){	$soma_comercializacao = $conexao->result("		SELECT SUM(z.valor_unidade) as soma_comercializacao		FROM produto as z		INNER JOIN comercializacao_direta as a ON z.id = a.id_produto		INNER JOIN cardapios_comercializacao_direta as b on a.id = b.id_comercializacao_direta		INNER JOIN historico_conta as c on c.id_item_cardapio = b.id		WHERE c.id_conta = $id_conta AND c.id_referencia_cardapio = 1	");		$soma_1 = (!empty($soma_comercializacao)) ? $soma_comercializacao[0]['soma_comercializacao'] : 0 ;	$soma_prato = $conexao->result("		SELECT SUM(z.custo_preparo) as soma_prato		FROM prato as z		INNER JOIN cardapios_pratos as a ON z.id = a.id_prato		INNER JOIN historico_conta as b on b.id_item_cardapio = a.id		WHERE b.id_conta = $id_conta AND b.id_referencia_cardapio = 0	");		$soma_2 = (!empty($soma_prato)) ? $soma_prato[0]['soma_prato'] : 0 ;		$valor_total = $soma_1 + $soma_2;		$update = $conexao->execute("		UPDATE conta		SET valor_total = $valor_total		WHERE id = $id_conta AND situacao = 1	");}?>